package chapter05;

public class Ex8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 0;
		

		// num 변수에 들어있는 값이 0이라면
		if (num == 0) {
			System.out.println("num 변수에 들어있는 값은 0 입니다");
		}
		
		
		else {
		// num 변수에 들어있는 값이 0이 아니라면
		// (num변수에 들어있는 값이 음수 또는 양수라면)
		// 위와 같이 조건을 붙여서 else 를 생각하면 안됌 
			System.out.println("num 변수에 들어있는 값은 0이 아닙니다.");
		}
		

	}
	
}
