package chapter06;

public class Ex01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// num이 1부터
		int num =1; 
		
		// 3이하일 동안
		while (num <= 3) {
			//하나씩 증가하면서 while 안의 코드를 반복적으로 실행한다. 
			System.out.println("hello world~!");
			num++;

		}
		
		System.out.println("num = " + num);
	}

}
